function resultsWithTriggerID = AddIDToResults(_trimmedResults, _noiseGroupsNum, _distGroupsPerNoiseNum, _trigPerDistGroup)  numOfColumns = columns(_trimmedResults);  noiseColumn = numOfColumns + 1;  distanceColumn = noiseColumn + 1;  triggerColumn = distanceColumn + 1;  curNoise = 0;  curDist = 0;  curTrig = 0;  _noiseGroupsNum = int32(_noiseGroupsNum);  _distGroupsPerNoiseNum = int32(_distGroupsPerNoiseNum);  _trigPerDistGroup = int32(_trigPerDistGroup);  curTrigCounter = 0;    for curNoise = 1:_noiseGroupsNum        for curDist = 1:_distGroupsPerNoiseNum            for curTrig = 1:_trigPerDistGroup      ++curTrigCounter;      resultsWithTriggerID(curTrigCounter, 1:numOfColumns) = _trimmedResults(curTrigCounter, 1:numOfColumns);      resultsWithTriggerID(curTrigCounter, noiseColumn) = curNoise;      resultsWithTriggerID(curTrigCounter, distanceColumn) = curDist;      resultsWithTriggerID(curTrigCounter, triggerColumn) = curTrig;      endfor          endfor      endfor  
endfunction
