function resultsWithTriggerID = AddIDToResults(_trimmedResults, _noiseGroupsNum, _distGroupsPerNoiseNum, _trigPerDistGroup)  numOfColumns = columns(_trimmedResults);  numOfRows = rows(_trimmedResults);  noiseColumn = numOfColumns + 1;  distanceColumn = noiseColumn + 1;  triggerColumn = distanceColumn + 1;  curNoise = 0;  curDist = 0;  curTrig = 0;  triggerMod = _trigPerDistGroup;  distanceDiv = _distGroupsPerNoiseNum;  noiseDiv = _distGroupsPerNoiseNum * _trigPerDistGroup;    noiseStrNum = num2str(_noiseGroupsNum);  newNoiseGroupNum = str2num(noiseStrNum);      _noiseGroupsNum = int32(_noiseGroupsNum);  _distGroupsPerNoiseNum = int32(_distGroupsPerNoiseNum);  _trigPerDistGroup = int32(_trigPerDistGroup);  curTrigCounter = 0;    for curNoise = 1:_noiseGroupsNum        for curDist = 1:_distGroupsPerNoiseNum            for curTrig = 1:_trigPerDistGroup      ++curTrigCounter;      resultsWithTriggerID(curTrigCounter, 1:numOfColumns) = _trimmedResults(curTrigCounter, 1:numOfColumns);      resultsWithTriggerID(curTrigCounter, noiseColumn) = curNoise;      resultsWithTriggerID(curTrigCounter, distanceColumn) = curDist;      resultsWithTriggerID(curTrigCounter, triggerColumn) = curTrig;      endfor          endfor      endfor      ##  for curRow = 1:numOfRows##    curTrigCounter += 1;##    curTrig = 1 + rem(curTrigCounter - 1, triggerMod);##    curDist = 1 + rem(floor((curTrigCounter-1) / distanceDiv), distanceDiv);##    curNoise = 1 + floor((curTrigCounter-1) / noiseDiv );##    resultsWithTriggerID(curRow, 1:numOfColumns) = _trimmedResults(curRow, 1:numOfColumns);##    resultsWithTriggerID(curRow, noiseColumn) = curNoise;##    resultsWithTriggerID(curRow, distanceColumn) = curDist;##    resultsWithTriggerID(curRow, triggerColumn) = curTrig;##            ##    curTrigCounter += 1;##    curTrig = 1 + rem(curTrig, _trigPerDistGroup);##    curDist = 1 + (curTrigCounter / (_distGroupsPerNoiseNum * _trigPerDistGroup));##    curNoise = 1 + mod(curTrigCounter, _noiseGroupsNum + 1);##    resultsWithTriggerID(curRow, 1:numOfColumns) = _trimmedResults(curRow, 1:numOfColumns);##    resultsWithTriggerID(curRow, noiseColumn) = curNoise;##    resultsWithTriggerID(curRow, distanceColumn) = curDist;##    resultsWithTriggerID(curRow, triggerColumn) = curTrig;##    curNoise += 1;##    curDist += 1;##    curTrig += 1;    ##  endfor  
endfunction
